import { useState} from 'react';




function checkText(e, handleAnswer)
{

  let d = document.getElementById("write-box").value.toLowerCase();
  e = e.toLowerCase();

  if (d === e)
  {
    let ele = document.getElementById("write-box");

    ele.style.transition="color 2s"; 
    ele.style.color="green";
    
    console.log("right answer");
    handleAnswer(true);
  }
  // console.log("text was added " + d + ":" + e);

}

function answer(e, handleAnswer, setShowAns)
{
  if(e.key == 'Enter') { 
    // let ele = document.getElementById("write-box");

    document.querySelector("#write-box").classList.add('write-right-answer');

    // ele.style.transition="color 2s"; 
    // ele.style.color="red";
    // ele.style.readOnly = true;

    setShowAns(true);

    handleAnswer(false);
  }
}

export default function(props){
  let counter = props.counter;
  let pieces = props.pieces;
  let handleAnswer = props.handleAnswer;

  const [showAns, setShowAns] = useState(false); 

  console.log("write " +counter + " : " + pieces.length);
  var [que, ans] = pieces[counter].split(";");

  return (
    <> 
      <> 
      <div className='write-question'>
      <h1>{que}</h1>

      <input id="write-box" autoComplete="off" 
      placeholder='Insert text'
      onChange={() => checkText(ans, handleAnswer)} 
      onKeyDown={(e) => answer(e, handleAnswer, setShowAns)} />
      <h1>{showAns ? (
      <>
      {ans}
      </>
    ) : (
      <>
         
      </>
    )}
    </h1>
      </div>
      </>
    
    </>
  )


}
