    <h1 onClick={() => setShowAns(!showAns)}>{showAns ? (
      <>
      {q1}
      </>
    ) : (
      <>
         ?
      </>
    )}
    </h1>

    <h1></h1>
    <button onClick={() => setShowAns(!showAns)}>
      {showAns ? ("Hide Answer") : ("Show Answer")}
      </button>
    <h1></h1>

    </>
  )
}

const MulChoQuestion = (props) => {
  var f1 = pieces[props.counter+1].split(";")[1];
  var f2 = pieces[props.counter+2].split(";")[1];
  var f3 = pieces[props.counter+3].split(";")[1];
  return (
    <> 
    <h1>What is {props.question}?</h1>

    {props.showAns ? (
      <>
      <h1>It is {props.answer}</h1>
      </>
    ) : (
      <> 
        <h1> {props.answer}</h1>  
        <h1> {f1}</h1>  
        <h1> {f2}</h1>  
        <h1> {f3}</h1>  
      </>
    )}
    </>
  )
}


