const App = () => {

  const [counter, setCounter] = useState(0);

  useEffect(() => {

    if (counter > 0)
    {
      if (famWords.includes(counter))
      {
        qType = 1; 
        console.log(famWords);
      }
      else
      {
        qType = 0;
        famWords.push(counter);
        console.log(famWords);

      }
    }
},[counter]);

  return (
    <div className="App">
    <h1>nigga</h1>
    {qType == 0 ?(
      <FlashCard counter={counter}/>
    )
      :(
        <MulChoQuestion counter={counter}/>
      )}

    <button onClick={() => setCounter((prevCounter) => prevCounter-1)}>-</button>
    <h1>{counter}</h1>
    <button onClick={() => setCounter((prevCounter) => prevCounter+1)}>+</button>
    <h1></h1>
    <button onClick={() => setCounter(Math.floor(Math.random() * (pieces.length)))}>Randomize</button>

    </div>
  );
}

