let words_in_this_round =0;
let words_this_round = [];
let new_words_in_round =0;

var done = false;


function find_new_word()
{
  console.log(r1_words);
  let new_word = r1_words.length;
  for (let i = 0 ; i < pieces.length ; i++)
    {
      if (!words_in_this_round.includes(new_word))
