  }
  // console.log("text was added " + d + ":" + e);

}

function answer(e, handleAnswer)
{
  if(e.key == 'Enter') {
    handleAnswer(false);
  }
}

export default function(props){
  let counter = props.counter;
  let pieces = props.pieces;
  let handleAnswer = props.handleAnswer;

  console.log("write " +counter + " : " + pieces.length);
  var [que, ans] = pieces[counter].split(";");

  return (
    <> 
      <> 
      <div className='write-question'>
      <h1>What is {que}?</h1>

      <input id="write-box" onChange={() => checkText(ans, handleAnswer)} onKeyDown={(e) => answer(e, handleAnswer)} />
      </div>
      </>
    
    </>
  )


}
