const  pieces = terms.split(";;;");
var r1_words =[];
var r2_words =[];
let words_in_this_round =0;
let new_words_in_round =0;

var done = false;


const FlashCard = (props) => {

  var counter = props.counter;
  const [showAns, setShowAns] = useState(false); 

  var term = pieces[0].split(";");

  const [a1, setA1] = useState(term[0]);
  //var a1=term[0];
  const [q1, setQ1] = useState(term[1]);

  useEffect(() => {

      if (counter > 0)
      {
      var new_term = pieces[counter].split(";");
      // var a1=term[0];
    setA1(new_term[0]);
    setQ1(new_term[1]); 
    setShowAns(false);
    console.log("me");
  }
  },[counter]);
  return (
    <> 
    <h1>{a1}</h1>
    <h1 onClick={() => setShowAns(!showAns)}>{showAns ? (
      <>
      {q1}
      </>
    ) : (
      <>
         ?
      </>
    )}
    </h1>

    <h1></h1>
    <button onClick={() => setShowAns(!showAns)}>
      {showAns ? ("Hide Answer") : ("Show Answer")}
      </button>
    <h1></h1>

    </>
  )
}

function generate_answers_list(a1, a2, a3, a4)
{
  var rand_ind = Math.floor(Math.random() * (2*3*4));
  var org = [a1, a2, a3, a4]; 
  var result =[];
  var facs =[1,1,2,6];

  for (const v of [1,2,3,4])
  {

    var fac = facs[org.length-1];
    var cur_ind = Math.floor(rand_ind/fac);
    // console.log("rand_ind: " + rand_ind + " fac: " + fac);
    result.push(org[cur_ind]);
    org.splice(cur_ind, 1)
    rand_ind = rand_ind % fac;

  }

  return result;
}

function check_answer(answer_ind, right_answer_ind)
{
  if (answer_ind == right_answer_ind) 
  {
    let ele = document.getElementById("ans"+answer_ind)

    ele.style.transition="color 2s"; 
    document.getElementById("ans"+answer_ind).style.color="green";
    console.log("true answer");
  }
  else 
  {
    let ele = document.getElementById("ans"+answer_ind)

    ele.style.transition="color 2s"; 
    document.getElementById("ans"+answer_ind).style.color="magenta";
    console.log("false answer");

  }
}

const MulChoQuestion = (props) => {
  var counter = props.counter;
  var [que, ans] = pieces[counter].split(";");
  var [i0, i1,i2,i3] = [0, 1,2,3];
  if (counter > pieces.length - 3) 
    [i0, i1,i2,i3] = [-3, -2,-1,0];
  var f0 = pieces[counter+i0].split(";")[1];
  var f1 = pieces[counter+i1].split(";")[1];
  var f2 = pieces[counter+i2].split(";")[1];
  var f3 = pieces[counter+i3].split(";")[1];
  var ans_list = generate_answers_list(f0,f1,f2,f3);
  var ans_ind = ans_list.indexOf(ans);
  console.log("answer is in " + ans_ind);
  for (let i =0;i<4; i++)
  {
    console.log(i)
    if (document.getElementById("ans"+i)!= null)
    {
      let ele = document.getElementById("ans"+i)
      ele.style.transition="color 0s";
      // flushCss(ele);
      ele.style.color="white";
      // ele.style.transition="color 2s"; 
      // flushCss(ele);
    }
  }
  // var ans_list;
  // for (let i =0; i < 24; i++)
  // {
  //   // var gen= generate_answers_list(ans,f1,f2,f3, i);
  //   ans_list= generate_answers_list(ans,f1,f2,f3, i);
  //   ans_list.push(i);
  //   console.log(ans_list);
  // }

  return (
    <> 
      <> 
      <div className='question-card'>
