        // <MulChoQuestion counter={counter} pieces = {pieces}/>
      )}
    <h1></h1>
    <button onClick={() => setCounter((prevCounter) => prevCounter-1)}>-</button>
    <h1>{counter}</h1>
    <button onClick={() => setCounter((prevCounter) => prevCounter+1)}>+</button>
    <h1></h1>
    <button onClick={() => setCounter(Math.floor(Math.random() * (pieces.length)))}>Randomize</button>

